import BigNumber from 'bignumber.js';
import { DeSerializers } from './de-serializers';
/**
 * Type of the default (de-)serializers.
 */
export type DefaultDeSerializers = DeSerializers<string, boolean, number, BigNumber, number, number, number, number, BigNumber, string, number, number, string, any>;
/**
 * @internal
 * Default (de-)serializers without `null` and `undefined` handling.
 */
export declare const defaultDeSerializersRaw: DefaultDeSerializers;
/**
 * @internal
 * Wraps the given default (de-)serialization functions with a check for nullish values.
 * @param deSerializers - (De-)Serializers to wrap.
 * @returns The wrapped (de-)serializers.
 */
export declare function wrapDefaultDeSerializers<DeSerializersT extends DeSerializers>(deSerializers: DeSerializersT): DeSerializersT;
/**
 * @internal
 * Default (de-)serializers without `null` and `undefined` handling.
 */
export declare const defaultDeSerializers: DefaultDeSerializers;
